import { useState, useEffect } from "react";
import { useLocation, useNavigate, unstable_useBlocker as useBlocker } from "react-router-dom"; 
// Note: React Router v6.4+ uses unstable_useBlocker or usePrompt pattern, check your version

import axios from "axios";
import Navbar from "../../components/Navbar";

function PlayerQuiz() {
  const location = useLocation();
  const navigate = useNavigate();
  const { categoryId, questionCount } = location.state || {};

  const [questions, setQuestions] = useState([]);
  const [currentAnswers, setCurrentAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [score, setScore] = useState(0);

  // Block all navigation except when quizCompleted
  useBlockNavigation(!quizCompleted);

  useEffect(() => {
    if (!categoryId || !questionCount) {
      navigate("/player/dashboard", { replace: true });
      return;
    }

    const fetchQuestions = async () => {
      try {
        setLoading(true);
        const response = await axios.get("http://localhost:5000/api/questions/quiz", {
          params: { categoryId, count: questionCount },
        });
        setQuestions(response.data);
      } catch (error) {
        setError("Failed to load questions");
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();

    // Push a new state to block back button
    window.history.pushState(null, "", window.location.href);

    // Popstate listener silently re-pushes state to block back navigation
    const onPopState = () => {
      if (!quizCompleted) {
        window.history.pushState(null, "", window.location.href);
      }
    };
    window.addEventListener("popstate", onPopState);

    // Beforeunload listener silently prevents page close/refresh
    const onBeforeUnload = (e) => {
      if (!quizCompleted) {
        e.preventDefault();
        e.returnValue = "";
      }
    };
    window.addEventListener("beforeunload", onBeforeUnload);

    return () => {
      window.removeEventListener("popstate", onPopState);
      window.removeEventListener("beforeunload", onBeforeUnload);
    };
  }, [categoryId, questionCount, navigate, quizCompleted]);

  function useBlockNavigation(block) {
    // React Router v6 approach to block navigation
    // https://reactrouter.com/en/main/hooks/use-blocker
    useEffect(() => {
      if (!block) return;

      const unblock = navigate.block((tx) => {
        // Block navigation silently if quiz is not completed
        if (!quizCompleted) {
          // do nothing, just block
          return;
        }
        unblock();
        tx.retry();
      });

      return unblock;
    }, [block, navigate]);
  }

  const handleAnswerSelect = (questionId, optionIndex) => {
    if (quizCompleted) return;

    setCurrentAnswers((prev) => ({
      ...prev,
      [questionId]: optionIndex,
    }));

    const question = questions.find((q) => q._id === questionId);
    if (question.correctOption === optionIndex) {
      setScore((prev) => prev + 1);
    }
  };

  const handleSubmitQuiz = async () => {
    try {
      const token = localStorage.getItem("token");

      const results = questions.map((question) => ({
        questionId: question._id,
        selectedOption:
          currentAnswers[question._id] !== undefined
            ? currentAnswers[question._id]
            : null,
        isCorrect: currentAnswers[question._id] === question.correctOption,
      }));

      await axios.post(
        "http://localhost:5000/api/quiz/submit",
        {
          categoryId,
          results,
          score,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setQuizCompleted(true);
    } catch (error) {
      setError("Failed to submit quiz");
      console.error(error);
    }
  };

  const handleReturnToDashboard = () => {
    navigate("/player/dashboard");
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100">
        <Navbar />
        <div className="container mx-auto px-4 py-8">
          <div className="text-center">Loading quiz questions...</div>
        </div>
      </div>
    );
  }

  if (quizCompleted) {
    return (
      <div className="min-h-screen bg-gray-100">
        <Navbar />
        <div className="container mx-auto px-4 py-8">
          <div className="bg-white rounded-lg shadow-md p-6 max-w-2xl mx-auto text-center">
            <h1 className="text-3xl font-bold mb-6 text-purple-600">Quiz Completed!</h1>
            <p className="text-xl mb-4">
              Your score: {score} out of {questions.length}
            </p>
            <p className="text-lg mb-6">
              {score === questions.length
                ? "Perfect score! Amazing job!"
                : score > questions.length / 2
                ? "Good job! Keep practicing to improve."
                : "Keep practicing to improve your score."}
            </p>
            <button
              onClick={handleReturnToDashboard}
              className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Return to Dashboard
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-md p-6 max-w-3xl mx-auto">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-bold text-purple-600">Quiz in Progress</h1>
            <div className="text-lg font-semibold">
              Score: {score} / {questions.length}
            </div>
          </div>

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
              {error}
            </div>
          )}

          <div className="space-y-8">
            {questions.map((question, questionIndex) => (
              <div key={question._id} className="border rounded-lg p-4">
                <h3 className="text-lg font-semibold mb-3">
                  {questionIndex + 1}. {question.text}
                </h3>

                <div className="space-y-2">
                  {question.options.map((option, optionIndex) => {
                    const isSelected = currentAnswers[question._id] === optionIndex;
                    return (
                      <div
                        key={optionIndex}
                        onClick={() => handleAnswerSelect(question._id, optionIndex)}
                        className={`p-3 rounded-md cursor-pointer border transition-colors ${
                          isSelected
                            ? "bg-blue-100 border-blue-400"
                            : "hover:bg-gray-100 border-gray-300"
                        }`}
                      >
                        <div className="flex items-start">
                          <div className="flex-shrink-0 w-6">
                            {String.fromCharCode(65 + optionIndex)}.
                          </div>
                          <div className="ml-2">{option}</div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            ))}
          </div>

          <div className="mt-8 flex justify-end">
            <button
              onClick={handleSubmitQuiz}
              className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-6 rounded focus:outline-none focus:shadow-outline"
            >
              Submit Quiz
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PlayerQuiz;
