import { useState, useEffect, useRef } from 'react'
import {
  Link,
  useLocation,
  useNavigate,
  useParams,
  useSearchParams,
} from 'react-router-dom'
import axios from '../../utils/axiosConfig'
import { useAuth } from '../../contexts/AuthContext'
import { toast } from 'react-toastify'

// âœ… Internal Navbar Component (with disable logic)
function Navbar({ disableNav }) {
  const { user, logout } = useAuth()
  const navigate = useNavigate()

  const handleLogout = () => {
    logout()
    navigate('/login')
  }

  const handleNavClick = (e, to) => {
    if (disableNav) {
      e.preventDefault()
      alert('You cannot navigate away during the quiz.')
    } else {
      navigate(to)
    }
  }

  return (
    <nav className="bg-purple-700 text-white shadow-md">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center py-4">
          <div className="flex items-center">
            <Link
              to={
                user?.role === 'admin'
                  ? '/admin/dashboard'
                  : '/player/dashboard'
              }
              onClick={(e) =>
                handleNavClick(
                  e,
                  user?.role === 'admin'
                    ? '/admin/dashboard'
                    : '/player/dashboard'
                )
              }
              className="text-xl font-bold"
            >
              Quiz Game
            </Link>
          </div>

          {user && (
            <div className="flex items-center space-x-6">
              {user.role === 'player' ? (
                <>
                  <Link
                    to="/player/dashboard"
                    onClick={(e) => handleNavClick(e, '/player/dashboard')}
                    className="hover:text-purple-200"
                  >
                    Dashboard
                  </Link>
                  <Link
                    to="/player/history"
                    onClick={(e) => handleNavClick(e, '/player/history')}
                    className="hover:text-purple-200"
                  >
                    My History
                  </Link>
                </>
              ) : (
                <>
                  <Link
                    to="/admin/dashboard"
                    onClick={(e) => handleNavClick(e, '/admin/dashboard')}
                    className="hover:text-purple-200"
                  >
                    Dashboard
                  </Link>
                  <Link
                    to="/admin/questions"
                    onClick={(e) => handleNavClick(e, '/admin/questions')}
                    className="hover:text-purple-200"
                  >
                    Questions
                  </Link>
                  <Link
                    to="/admin/leaderboard"
                    onClick={(e) => handleNavClick(e, '/admin/leaderboard')}
                    className="hover:text-purple-200"
                  >
                    Leaderboard
                  </Link>
                  <Link
                    to="/admin/players"
                    onClick={(e) => handleNavClick(e, '/admin/players')}
                    className="hover:text-purple-200"
                  >
                    Players
                  </Link>
                </>
              )}

              <div className="flex items-center space-x-2">
                <span className="text-sm">{user.name}</span>
                <button
                  onClick={handleLogout}
                  className="bg-purple-800 hover:bg-purple-900 text-white text-sm py-1 px-3 rounded"
                >
                  Logout
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </nav>
  )
}

function Quiz() {
  const { categoryId } = useParams()
  const [searchParams] = useSearchParams()
  const difficulty = searchParams.get('difficulty') || 'medium'
  const navigate = useNavigate()
  const { user } = useAuth()
  const mounted = useRef(true)

  const [questions, setQuestions] = useState([])
  const [userAnswers, setUserAnswers] = useState({})
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState('')
  const [quizSubmitted, setQuizSubmitted] = useState(false)
  const [score, setScore] = useState(0)
  const [stats, setStats] = useState({
    easy: { correct: 0, total: 0 },
    medium: { correct: 0, total: 0 },
    hard: { correct: 0, total: 0 },
  })

  useEffect(() => {
    return () => {
      mounted.current = false
    }
  }, [])

  useEffect(() => {
    if (!categoryId || !user) {
      navigate('/player/dashboard')
      return
    }

    const fetchData = async () => {
      try {
        setLoading(true)
        const response = await axios.post(`/api/quiz/questions`, {
          categoryId,
          difficulty,
          count: 10,
        })

        if (!mounted.current) return

        if (!response.data || response.data.length === 0) {
          throw new Error(
            'No questions available for this category and difficulty'
          )
        }

        setQuestions(response.data)
        setError('')
      } catch (err) {
        if (!mounted.current) return
        const message = err.response?.data?.message || err.message
        setError(message)
        toast.error(message)
        if (err.response?.status === 401) {
          navigate('/login')
        } else {
          navigate('/player/dashboard')
        }
      } finally {
        if (mounted.current) {
          setLoading(false)
        }
      }
    }

    fetchData()
  }, [categoryId, difficulty, navigate, user])

  const calculatePoints = (difficulty) => {
    switch (difficulty) {
      case 'easy':
        return 1
      case 'medium':
        return 2
      case 'hard':
        return 3
      default:
        return 1
    }
  }

  const handleAnswer = (questionId, selectedOption) => {
    setUserAnswers((prev) => ({
      ...prev,
      [questionId]: selectedOption,
    }))
  }

  const handleSubmit = async () => {
    // Check if all questions are answered
    const unansweredCount = questions.length - Object.keys(userAnswers).length
    if (unansweredCount > 0) {
      toast.warning(
        `Please answer all questions. ${unansweredCount} remaining.`
      )
      return
    }

    try {
      // Calculate score and stats
      let totalScore = 0
      const newStats = {
        easy: { correct: 0, total: 0 },
        medium: { correct: 0, total: 0 },
        hard: { correct: 0, total: 0 },
      }

      const formattedAnswers = questions.map((question) => {
        const isCorrect = userAnswers[question._id] === question.correctOption
        const points = isCorrect ? calculatePoints(question.difficulty) : 0
        totalScore += points

        // Update stats
        newStats[question.difficulty].total += 1
        if (isCorrect) {
          newStats[question.difficulty].correct += 1
        }

        return {
          questionId: question._id,
          selectedOption: userAnswers[question._id],
          isCorrect,
          points,
          difficulty: question.difficulty,
        }
      })

      // Submit to backend
      await axios.post('/api/quiz/submit', {
        categoryId,
        answers: formattedAnswers,
        score: totalScore,
        stats: newStats,
      })

      setScore(totalScore)
      setStats(newStats)
      setQuizSubmitted(true)
      toast.success('Quiz submitted successfully!')
    } catch (error) {
      toast.error('Failed to submit quiz. Please try again.')
      console.error(error)
    }
  }

  const getDifficultyColor = (difficulty) => {
    switch (difficulty) {
      case 'easy':
        return 'text-green-600'
      case 'medium':
        return 'text-yellow-600'
      case 'hard':
        return 'text-red-600'
      default:
        return 'text-gray-600'
    }
  }

  const getOptionStyle = (question, optionValue) => {
    if (!quizSubmitted) {
      return userAnswers[question._id] === optionValue
        ? 'bg-blue-100 border-blue-500'
        : 'hover:bg-gray-50'
    }

    if (question.correctOption === optionValue) {
      return 'bg-green-100 border-green-500'
    }
    if (userAnswers[question._id] === optionValue) {
      return question.correctOption === optionValue
        ? 'bg-green-100 border-green-500'
        : 'bg-red-100 border-red-500'
    }
    return ''
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100">
        <Navbar disableNav={true} />
        <div className="container mx-auto px-4 py-8">
          <div className="text-center">Loading questions...</div>
        </div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-100">
        <Navbar disableNav={true} />
        <div className="container mx-auto px-4 py-8">
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
            {error}
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar disableNav={!quizSubmitted} />
      <div className="container mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-bold text-gray-800">
              Quiz - {questions[0]?.category?.name}
            </h1>
            <span className={`font-semibold ${getDifficultyColor(difficulty)}`}>
              {difficulty.charAt(0).toUpperCase() + difficulty.slice(1)}
            </span>
          </div>

          {quizSubmitted && (
            <div className="mb-6 p-4 bg-gray-50 rounded-lg">
              <h2 className="text-xl font-bold mb-2">Quiz Results</h2>
              <p className="text-lg">Total Score: {score}</p>
              <div className="mt-2">
                {Object.entries(stats).map(([diff, stat]) => (
                  <p key={diff} className={`${getDifficultyColor(diff)}`}>
                    {diff.charAt(0).toUpperCase() + diff.slice(1)}:{' '}
                    {stat.correct}/{stat.total} correct
                  </p>
                ))}
              </div>
            </div>
          )}

          <div className="space-y-6">
            {questions.map((question, index) => (
              <div
                key={question._id}
                className="p-4 border rounded-lg hover:shadow-sm transition-shadow"
              >
                <p className="text-lg font-medium mb-4">
                  {index + 1}. {question.text}
                </p>
                <div className="space-y-2">
                  {question.options.map((option, optionIndex) => (
                    <button
                      key={optionIndex}
                      onClick={() =>
                        !quizSubmitted && handleAnswer(question._id, option)
                      }
                      className={`w-full text-left p-3 border rounded-md transition-colors ${getOptionStyle(
                        question,
                        option
                      )}`}
                      disabled={quizSubmitted}
                    >
                      {option}
                    </button>
                  ))}
                </div>
              </div>
            ))}
          </div>

          {!quizSubmitted && (
            <div className="mt-6">
              <button
                onClick={handleSubmit}
                className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition-colors"
              >
                Submit Quiz
              </button>
            </div>
          )}

          {quizSubmitted && (
            <div className="mt-6">
              <button
                onClick={() => navigate('/player/dashboard')}
                className="w-full bg-gray-600 text-white py-2 px-4 rounded-md hover:bg-gray-700 transition-colors"
              >
                Back to Dashboard
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default Quiz
