import { useState, useEffect } from "react";
import { useLocation, useNavigate, Link } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext"; // adjust path as needed

function PlayerQuiz() {
  const location = useLocation();
  const navigate = useNavigate();
  const { categoryId, questionCount } = location.state || {};
  const { user, logout } = useAuth();

  const [questions, setQuestions] = useState([]);
  const [currentAnswers, setCurrentAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [score, setScore] = useState(0);

  // Disable navigation while quiz is in progress
  const disableNav = !quizCompleted;

  // Handle logout
  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  useEffect(() => {
    if (!categoryId || !questionCount) {
      navigate("/player/dashboard", { replace: true });
      return;
    }

    const fetchQuestions = async () => {
      try {
        setLoading(true);
        const response = await axios.get("http://localhost:5000/api/questions/quiz", {
          params: { categoryId, count: questionCount },
        });
        setQuestions(response.data);
      } catch (error) {
        setError("Failed to load questions");
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();

    // Prevent back navigation and warn on page unload (same as your original)
    window.history.pushState(null, "", window.location.href);

    const onPopState = () => {
      if (!quizCompleted) {
        window.history.pushState(null, "", window.location.href);
      }
    };
    window.addEventListener("popstate", onPopState);

    const onBeforeUnload = (e) => {
      if (!quizCompleted) {
        e.preventDefault();
        e.returnValue = "";
      }
    };
    window.addEventListener("beforeunload", onBeforeUnload);

    return () => {
      window.removeEventListener("popstate", onPopState);
      window.removeEventListener("beforeunload", onBeforeUnload);
    };
  }, [categoryId, questionCount, navigate, quizCompleted]);

  const handleAnswerSelect = (questionId, optionIndex) => {
    if (quizCompleted) return;

    setCurrentAnswers((prev) => ({
      ...prev,
      [questionId]: optionIndex,
    }));

    const question = questions.find((q) => q._id === questionId);
    if (question.correctOption === optionIndex) {
      setScore((prev) => prev + 1);
    }
  };

  const handleSubmitQuiz = async () => {
    try {
      const token = localStorage.getItem("token");

      const results = questions.map((question) => ({
        questionId: question._id,
        selectedOption:
          currentAnswers[question._id] !== undefined
            ? currentAnswers[question._id]
            : null,
        isCorrect: currentAnswers[question._id] === question.correctOption,
      }));

      await axios.post(
        "http://localhost:5000/api/quiz/submit",
        {
          categoryId,
          results,
          score,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setQuizCompleted(true);
    } catch (error) {
      setError("Failed to submit quiz");
      console.error(error);
    }
  };

  // Embedded Navbar component with nav blocking logic
  const EmbeddedNavbar = () => {
    const handleNavClick = (e, path) => {
      // Block Dashboard and My History links when disableNav is true
      if (
        disableNav &&
        (path === "/player/dashboard" || path === "/player/history")
      ) {
        e.preventDefault();
        alert("You cannot leave the quiz until you complete it.");
      }
    };

    return (
      <nav className="bg-purple-700 text-white shadow-md">
        <div className="container mx-auto px-4">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center">
              <Link
                to={user?.role === "admin" ? "/admin/dashboard" : "/player/dashboard"}
                className="text-xl font-bold"
                onClick={(e) =>
                  disableNav && user?.role !== "admin"
                    ? (e.preventDefault(), alert("You cannot leave the quiz until you complete it."))
                    : null
                }
              >
                Quiz Game
              </Link>
            </div>

            {user && (
              <div className="flex items-center space-x-6">
                {user.role === "player" ? (
                  <>
                    <Link
                      to="/player/dashboard"
                      className="hover:text-purple-200"
                      onClick={(e) => handleNavClick(e, "/player/dashboard")}
                      style={disableNav ? { pointerEvents: "auto" } : {}}
                    >
                      Dashboard
                    </Link>
                    <Link
                      to="/player/history"
                      className="hover:text-purple-200"
                      onClick={(e) => handleNavClick(e, "/player/history")}
                      style={disableNav ? { pointerEvents: "auto" } : {}}
                    >
                      My History
                    </Link>
                  </>
                ) : (
                  <>
                    <Link to="/admin/dashboard" className="hover:text-purple-200">
                      Dashboard
                    </Link>
                    <Link to="/admin/questions" className="hover:text-purple-200">
                      Questions
                    </Link>
                    <Link to="/admin/leaderboard" className="hover:text-purple-200">
                      Leaderboard
                    </Link>
                    <Link to="/admin/players" className="hover:text-purple-200">
                      Players
                    </Link>
                  </>
                )}

                <div className="flex items-center space-x-2">
                  <span className="text-sm">{user.name}</span>
                  <button
                    onClick={handleLogout}
                    className="bg-purple-800 hover:bg-purple-900 text-white text-sm py-1 px-3 rounded"
                  >
                    Logout
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100">
        <EmbeddedNavbar />
        <div className="container mx-auto px-4 py-8">
          <div className="text-center">Loading quiz questions...</div>
        </div>
      </div>
    );
  }

  if (quizCompleted) {
    return (
      <div className="min-h-screen bg-gray-100">
        <EmbeddedNavbar />
        <div className="container mx-auto px-4 py-8 text-center">
          <h1 className="text-3xl font-bold mb-6 text-purple-600">Quiz Completed!</h1>
          <p className="text-xl mb-4">
            Your score: {score} out of {questions.length}
          </p>
          <button
            onClick={() => navigate("/player/dashboard")}
            className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded"
          >
            Return to Dashboard
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <EmbeddedNavbar />
      <div className="container mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-md p-6 max-w-3xl mx-auto">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-bold text-purple-600">Quiz in Progress</h1>
            <div className="text-lg font-semibold">
              Score: {score} / {questions.length}
            </div>
          </div>

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
              {error}
            </div>
          )}

          <div className="space-y-8">
            {questions.map((question, questionIndex) => (
              <div key={question._id} className="border rounded-lg p-4">
                <h3 className="text-lg font-semibold mb-3">
                  {questionIndex + 1}. {question.text}
                </h3>

                <div className="space-y-2">
                  {question.options.map((option, optionIndex) => {
                    const isSelected = currentAnswers[question._id] === optionIndex;
                    return (
                      <div
                        key={optionIndex}
                        onClick={() => handleAnswerSelect(question._id, optionIndex)}
                        className={`p-3 rounded-md cursor-pointer border transition-colors ${
                          isSelected
                            ? "bg-blue-100 border-blue-400"
                            : "hover:bg-gray-100 border-gray-300"
                        }`}
                      >
                        <div className="flex items-start">
                          <div className="flex-shrink-0 w-6">
                            {String.fromCharCode(65 + optionIndex)}.
                          </div>
                          <div className="ml-2">{option}</div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            ))}
          </div>

          <div className="mt-8 flex justify-end">
            <button
              onClick={handleSubmitQuiz}
              className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-6 rounded"
            >
              Submit Quiz
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PlayerQuiz;
