import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
import { useAuth } from '../../contexts/AuthContext'
import Navbar from '../../components/Navbar'
import { toast } from 'react-toastify'

function PlayerDashboard() {
  const { user } = useAuth()
  const navigate = useNavigate()
  const [categories, setCategories] = useState([])
  const [selectedCategory, setSelectedCategory] = useState('')
  const [selectedDifficulty, setSelectedDifficulty] = useState('medium')
  const [showQuizModal, setShowQuizModal] = useState(false)
  const [error, setError] = useState('')

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const token = localStorage.getItem('token')
        const response = await axios.get(
          'http://localhost:5000/api/categories',
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        )
        setCategories(response.data)
        if (response.data.length > 0) {
          setSelectedCategory(response.data[0]._id)
        }
      } catch (error) {
        setError('Failed to load categories')
        console.error(error)
      }
    }

    fetchCategories()
  }, [])

  const handleStartQuiz = () => {
    if (!selectedCategory) {
      toast.warning('Please select a category first')
      return
    }
    setShowQuizModal(true)
  }

  const confirmStartQuiz = () => {
    navigate('/player/quiz', {
      state: {
        categoryId: selectedCategory,
        difficulty: selectedDifficulty,
      },
    })
  }

  const handleViewHistory = () => {
    navigate('/player/history')
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-100">
        <Navbar />
        <div className="container mx-auto px-4 py-8">
          <div className="bg-white rounded-lg shadow-md p-6 max-w-2xl mx-auto">
            <h1 className="text-3xl font-bold text-center mb-6 text-purple-600">
              Welcome, {user?.name}!
            </h1>

            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
              {error}
            </div>

            <div className="mb-6">
              <h2 className="text-xl font-semibold mb-4">Start a New Quiz</h2>
              <div className="mb-4">
                <label
                  className="block text-gray-700 text-sm font-bold mb-2"
                  htmlFor="category"
                >
                  Select Category
                </label>
                <select
                  id="category"
                  value={selectedCategory}
                  onChange={(e) => setSelectedCategory(e.target.value)}
                  className="shadow border rounded w-full py-2 px-3 text-gray-700"
                >
                  {categories.map((category) => (
                    <option key={category._id} value={category._id}>
                      {category.name}
                    </option>
                  ))}
                </select>
              </div>

              <button
                onClick={handleStartQuiz}
                className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded w-full"
                disabled={!selectedCategory}
              >
                Start Quiz
              </button>
            </div>

            <div className="border-t pt-6">
              <button
                onClick={handleViewHistory}
                className="bg-gray-600 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded w-full"
              >
                View My History
              </button>
            </div>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-md p-6 max-w-2xl mx-auto">
          <h1 className="text-3xl font-bold text-center mb-6 text-purple-600">
            Welcome, {user?.name}!
          </h1>

          <div className="mb-6">
            <h2 className="text-xl font-semibold mb-4">Start a New Quiz</h2>
            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="category"
              >
                Select Category
              </label>
              <select
                id="category"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="shadow border rounded w-full py-2 px-3 text-gray-700"
              >
                {categories.map((category) => (
                  <option key={category._id} value={category._id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>

            <button
              onClick={handleStartQuiz}
              className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded w-full"
              disabled={!selectedCategory}
            >
              Start Quiz
            </button>
          </div>

          <div className="border-t pt-6">
            <button
              onClick={handleViewHistory}
              className="bg-gray-600 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded w-full"
            >
              View My History
            </button>
          </div>
        </div>
      </div>

      {/* Quiz Settings Modal */}
      {showQuizModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg shadow-lg p-6 max-w-md w-full">
            <h2 className="text-xl font-semibold mb-4">Quiz Settings</h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Select Difficulty
              </label>
              <select
                value={selectedDifficulty}
                onChange={(e) => setSelectedDifficulty(e.target.value)}
                className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="easy">Easy</option>
                <option value="medium">Medium</option>
                <option value="hard">Hard</option>
              </select>
            </div>

            <div className="flex justify-end space-x-3">
              <button
                onClick={() => setShowQuizModal(false)}
                className="px-4 py-2 border rounded hover:bg-gray-100"
              >
                Cancel
              </button>
              <button
                onClick={confirmStartQuiz}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
              >
                Start Quiz
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

export default PlayerDashboard
